import './App.css';
import {useEffect, useState} from "react";

function App() {
    const [isEmpty, setisEmpty]=useState(true);
    return(
        <div className={"mainbox"}>
            <input id={"input"} style={`width: ${20+12*document.getElementById("input").value.length}px`} className={isEmpty?"emptyinput":"nonemptyinput"} onInput={()=>{
                setisEmpty(document.getElementById("input").value==="")
            }} />
        </div>
    )
}

export default App;
